CREATE TABLE FTFileChunkHeader ( Id numeric(18,0) IDENTITY, SequenceLength numeric(18,0) NOT NULL, InternalMd5Hash char(32) NULL, ExternalMd5Hash char(32) NULL, FileName varchar(255) NOT NULL, ChunkTimeStamp numeric(18,0) NOT NULL, ClientId varchar(255) NULL
) LOCK DATAROWS WITH IDENTITY_GAP=1
CREATE UNIQUE INDEX FTFileChunkHeader01u ON FTFileChunkHeader(Id)

CREATE TABLE FTFileChunk ( Id numeric(18,0) IDENTITY, Content image NOT NULL, Ordinal numeric(18,0) NOT NULL, Md5Hash char(32) NULL, FileChunkHeaderId numeric(18,0) NOT NULL references FTFileChunkHeader(Id)
) LOCK DATAROWS WITH IDENTITY_GAP=1
CREATE UNIQUE INDEX FTFileChunk01u ON FTFileChunk(Id)

CREATE TABLE FTFileChunkHeader ( Id numeric(18,0) IDENTITY, SequenceLength numeric(18,0) NOT NULL, InternalMd5Hash char(32) NULL, ExternalMd5Hash char(32) NULL, FileName varchar(255) NOT NULL, ChunkTimeStamp numeric(18,0) NOT NULL, ClientId varchar(255) NULL
) LOCK DATAROWS WITH IDENTITY_GAP=1
CREATE UNIQUE INDEX FTFileChunkHeader01u ON FTFileChunkHeader(Id)

CREATE TABLE FTFileChunk ( Id numeric(18,0) IDENTITY, Content image NOT NULL, Ordinal numeric(18,0) NOT NULL, Md5Hash char(32) NULL, FileChunkHeaderId numeric(18,0) NOT NULL references FTFileChunkHeader(Id)
) LOCK DATAROWS WITH IDENTITY_GAP=1
CREATE UNIQUE INDEX FTFileChunk01u ON FTFileChunk(Id)

