CREATE TABLE ErrorCategorisationLink ( Id IDENTITY NOT NULL, ErrorCategorisationId NUMERIC NOT NULL, ModuleName VARCHAR(64) NOT NULL, FlowName VARCHAR(64) NOT NULL, FlowElementName VARCHAR(64) NOT NULL, Action VARCHAR(32) NOT NULL, ExceptionClass VARCHAR(255) NOT NULL );
ALTER TABLE ErrorCategorisationLink ADD FOREIGN KEY (ErrorCategorisationId) REFERENCES ErrorCategorisation(Id);
CREATE UNIQUE INDEX ErrorCategorisationLink01u ON ErrorCategorisationLink(ModuleName,FlowName,FlowElementName, Action, ExceptionClass)

CREATE TABLE ErrorCategorisation
					(
						Id             			IDENTITY NOT NULL,
						ErrorCategory 			VARCHAR(64) NOT NULL,
						ErrorDescription	 	VARCHAR(2048) NOT NULL
					);

CREATE TABLE Configuration
					(
					ConfigurationId
